import { Button } from '@material-ui/core'
import React, { useState } from 'react'
import { storage, db } from "./firebase"
import { ref } from "firebase/storage";
import firebase from 'firebase/compat/app';

function ImageUpload() {
    const [image, setImage] = useState(null);
    const [progress, setProgress] = useState(0);
    const [caption, setCaption] = useState('');

    const handleChange = (e) => {
        if(e.target.files[0]) { //get the first file user selected
            setImage(e.target.files[0]);
        }
    }

    const handleUpload = () => {
        const uploadTask = ref(storage, `images/${image.name}`).put(image);

        uploadTask.on(
            "state_changed",
            (snapshot) => {
                // progress function 
                const progress = Math.round((snapshot.bytesTransferred / snapshot.totalBytes) * 100); //progress indicator from 0 to 100
                setProgress(progress);
            },
            (error) => {
                // Error function
                console.log(error);
                alert(error.message);
            },
            () => {
                // complete function
                storage
                .ref("images")
                .child(image.name)
                .getDownloadURL()
                .then(url => {
                    // post image in db
                    db.collection("posts").add({
                        timestamp: firebase.getFirestore().FieldValue.serverTimestamp(), //we want the timestamp so that we can put recent posts at top regardless of where we are in the world
                        caption: caption,
                        imageUrl: url,
                        username: username
                    })
                })
            }
        )
    }

    return (
        <div>
            {/* Caption input + File Picker + Post button */}
            <input type="text" placeholder = 'Enter a caption...' onChange={event=> setCaption(event.target.value)}/>
            <input type="file" onChange={handleChange} />
            <Button className="imageupload__button" onClick={handleUpload}> 
                Upload
            </Button>
        </div>
    )
}

export default ImageUpload
